// ========================================
// Cache Check - TTL: 30 segundos
// ========================================

const CACHE_TTL_SECONDS = 30;
const CACHE_KEY = 'maquinas_all';

// Tentar obter do cache global
const cache = $workflow.staticData;

if (cache[CACHE_KEY]) {
  const age = (Date.now() - cache[CACHE_KEY].timestamp) / 1000;

  if (age < CACHE_TTL_SECONDS) {
    console.log(`âœ… Cache HIT (${age.toFixed(1)}s old)`);

    return {
      json: {
        fromCache: true,
        cacheAge: age,
        data: cache[CACHE_KEY].data
      }
    };
  }

  console.log(`âŒ Cache EXPIRED (${age.toFixed(1)}s old)`);
}

console.log('âŒ Cache MISS - fetching fresh data');

return {
  json: {
    fromCache: false,
    fetchFresh: true
  }
};

